var Api = require("api")
    ,testdata = require('./mockdata.js')
    ,util = require("util");
var MockApi = function(options){
    $this = this;
    Api.call($this);
}
util.inherits(MockApi,Api);
MockApi.prototype = {
    constructor:MockApi,
    getUsers:function(options){

        setTimeout(function(){
           var result = {data:testdata.users};
            options.callback(result);
        },3000);
    }
}

module.exports = MockApi;